import React, { Component } from 'react'

class details extends Component {

        state={
            firstName: "",
        }
        
        handleChange = (e)=>{
            this.setState({[e.target.name]: e.target.value})
        }




    validateForm= (e)=> {
        e.preventDefault()
        const {firstName, lastName,  cardNum} = this.state 
        let date = new Date();
        let month = date.getMonth();
        let year = date.getFullYear();
        let expiryMonth = document.getElementById("month").value;
        let expiryYear = document.getElementById("year").value;

        //validating the First and Last name
        if(firstName === "") {
            alert("First name cannot be empty");
            firstName.focus();
        }

        if(lastName.value === "") {
            alert("Last name cannot be empty");
            lastName.focus();
        }

        if(expiryMonth.selectedIndex === 0) {
            alert("Please select a month");
            return false;

        }

        if(expiryYear.selectedIndex === 0) {
            alert("Please select a year");
            return false;

        }

        // validating if the card is expired by checking the month and year
        var today, someday

        today = new Date();
        someday = new Date();
        someday.setFullYear(year, month -1, 1);

        if(someday < today) {
            alert("Enter a valid credit card");
            return false;
        }

        if(cardNum < 16 || cardNum > 16) {
            alert("length should be 16");
            cardNum.focus();
            return false;
        }

    }

    render() {
        return (
            <div className = "center_div">
            <form className = "details" onSubmit = {this.validateForm}>

            <div className = "form-inline">
                <input name = "cardNum" type = "tel" maxLength = "16" class = "form-control" placeholder = "Enter your card number*" required/>
            </div>

            <div className = "row">
                <div class = "col">
                    <input name = "month" type = "tel" maxLength = "2" class = "form-control" placeholder = "Month of expiry*" required/>
                </div>

                <div class = "col">
                    <input name = "year" type = "tel" maxLength = "2" class = "form-control" placeholder = "Year of expiry*" required/>
                </div>

                <div class = "col">
                    <input name = "cvv" type = "password" maxLength = "3" class = "form-control" placeholder = "Enter your CVV*" required/>
                </div>
                <p></p>
                

                
            </div>

            
            <div className = "row">
                <div class = "col">
                    <input  name="firstName" type = "text" class = "form-control" placeholder = "Enter your first name*" required onChange={this.handleChange}/>
                    </div>

                    <div class = "col">
                    <input name = "lastName" type = "text" class = "form-control" placeholder = "Enter your last name*" required/>
                </div>
                
            </div>

            <div class = "primary-button">
                <button type = "submit"  class = "btn btn-primary" > Submit </button>
            </div>

               
            </form>

            </div>
        )
    }
}

export default details;
